mapper.select:
SELECT e.entity_id, e.entity_name
FROM concepts c, entities e, entity_attr a

    c.concept_name = #{arg1} and c.c_id = e.concept_id  and e.entity_id = a.entity_id and attr_value = #{arg0}

SELECT e.entity_id, e.entity_name, c.concept_name AS attr_name, a.attr_value FROM concepts c, entities e, entity_attr a
a.attr_id = c.c_id and a.entity_id = e.entity_id and e.entity_name = #{arg0} and c.concept_name in
#{attr}
